#functions - addition, subtraction, multiplication, and division

def mult(a,b):
	mult_answer = a*b;
	return mult_answer;

def div(a,b):
	div_answer = a/b;
	return div_answer;

def add(a,b):
	add_answer = a+b;
	return add_answer;

def subt(a,b):
	subt_answer = a-b;
	return subt_answer;

	
