import timeimport os, sys, requestsfrom contextlib import closingfrom urllib.request import urlopenfrom urllib.parse import urlencode, quotemy_version = "2.3"def checkVersion():    ver = requests.get('https://raw.githubusercontent.com/TeamMusiBat/404/master/404.py').text.replace("\n","").replace(" ","")    if ver != my_version:        os.system('clear')        sys.exit()def make_tiny(url):    request_url = ('http://tinyurl.com/api-create.php?' + urlencode({'url':url}))         with closing(urlopen(request_url)) as response:        return response.read().decode('utf-8 ')def getinfo(usrmid):    checkVersion()    plinq = "line://nv/profilePopup/mid={}".format(usrmid)    output = make_tiny(plinq)    return outputdef getDate(cuTime):    _crtTime = int(cuTime)    outdate = time.strftime('%Y-%m-%d', time.localtime(_crtTime))    return outdate